import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/images/capsule.jpg';


export const Intro = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const timeout = setTimeout(() => {
      navigate('/main-menu'); // Redirect to the MainMenu after 5 seconds
    }, 5000);

    return () => clearTimeout(timeout);
  }, [navigate]);

  return (
    <div className="flex items-center justify-center bg-black">
      <img src={logo} alt="logo" className="w-4 h-4 animate-spin" /> {/* Adjusted the size */}
      <p className="text-white text-xl ml-4 font-mario">Presented By </p>
      <p className="text-white text-5xl ml-4 font-mario">Nexrage Games </p>
    </div>
  );
};
