import React, { useRef, useState } from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

interface CarouselProps {
  imageList: string[];
}

export const Carousel: React.FC<CarouselProps> = ({ imageList }) => {
  const carousel = useRef(null);
  const [focusedImage, setFocusedImage] = useState(imageList[0]);
  
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 2000,
    beforeChange: (current, next) => setFocusedImage(imageList[next]),
  };

  const handleClick = (index: number) => {
    // Perform any action when an image is clicked
    setFocusedImage(imageList[index]);
  };

  const handleButtonClick = () => {
    // Navigate to the image that's in focus
    if (carousel && carousel.current) {
      const index = imageList.indexOf(focusedImage);
      (carousel.current as any).slickGoTo(index);
    }
  };

  return (
    <div className='w-screen h-screen flex flex-col items-center justify-center'>
      <div className='flex-1 w-full'>
        <Slider ref={carousel} {...settings}>
          {imageList.map((imageUrl, index) => (
            <div key={index} onClick={() => handleClick(index)} className="w-full h-full flex items-center justify-center overflow-hidden">
              <img className="w-full h-full object-cover" src={imageUrl} alt={`carousel ${index + 1}`} />
            </div>
          ))}
        </Slider>
      </div>
      <button onClick={handleButtonClick} className="w-1/2 h-12 mt-4 bg-blue-500 text-white rounded shadow">Go to selected image</button>
    </div>
  );
};
