import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Circle = () => (
  <svg
    viewBox="0 0 100 100"
    className="w-16 h-16"
  >
    <circle cx="50" cy="50" r="50" fill="black" />
    <path d="M50 0 A50 50 0 0 1 50 100 A50 50 0 0 1 50 0" fill="white" />
  </svg>
);

export const Intro = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const timeout = setTimeout(() => {
      navigate('/'); // Redirect to the MainMenu after 5 seconds
    }, 5000);

    return () => clearTimeout(timeout);
  }, [navigate]);

  return (
    <div className="flex items-center justify-center bg-black h-screen">
      <div className="animate-spin">
      <Circle /> {/* Use the Circle component directly */}
      </div>
      <p className="text-white text-xl ml-4">Presented by Nexrage Games</p>
    </div>
  );
};
