import React,{useState} from 'react';
import { ProfileCard } from '../ProfileCard/ProfileCard';

const ProfileContainer = () => {
  const [description, setDescription] = useState('Default description here.');
  const descriptionOptions = ['Option 1', 'Option 2', 'Option 3']; // Add your own options

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  return (
    <div className="flex flex-col h-screen justify-center items-center bg-gray-100">
      <div className="flex w-full max-w-6xl bg-white rounded-lg shadow-lg p-6">
        <div className="w-2/3">
          <ProfileCard description={description} />
          <div className="mt-4">
            <label htmlFor="description" className="text-sm font-bold">Change Description:</label>
            <select
              id="description"
              value={description}
              onChange={handleDescriptionChange}
              className="w-full border rounded p-2 mt-1"
            >
              {descriptionOptions.map((option) => (
                <option key={option} value={option}>
                  {option}
                </option>
              ))}
            </select>
          </div>
          <div className="mt-4">
            <button className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600">
              Change Profile Card
            </button>
          </div>
        </div>
        <div className="w-1/3 flex flex-col items-center justify-center border-l border-gray-200 pl-6">
          <img
            src="avatar-url.jpg" // Replace with the actual avatar URL
            alt="Avatar"
            className="w-24 h-24 rounded-full border-2 border-gray-300"
          />
          <button className="w-full bg-green-500 text-white p-2 rounded mt-4 hover:bg-green-600">
            Option 1
          </button>
          <button className="w-full bg-green-500 text-white p-2 rounded mt-4 hover:bg-green-600">
            Option 2
          </button>
          <button className="w-full bg-green-500 text-white p-2 rounded mt-4 hover:bg-green-600">
            Option 3
          </button>
        </div>
      </div>
    </div>
  );
};
