import React, { useEffect, useState } from "react";

interface IntroProps {
  onEnd: () => void;
}

export const Intro: React.FC<IntroProps> = ({ onEnd }) => {
  const [fadeToBlack, setFadeToBlack] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setFadeToBlack(true);
      setTimeout(onEnd, 1000); // After 1 second of fading to black, call the onEnd callback
    }, 3000); // Wait 3 seconds before starting the fade

    return () => clearTimeout(timer);
  }, [onEnd]);

  return (
    <div className={`flex flex-col justify-center items-center h-screen transition-all duration-1000 ${fadeToBlack ? "bg-black" : "bg-white"}`}>
      <div className="flex flex-col items-center space-y-4">
        <img src="/path/to/your/logo.png" alt="Logo" className="w-16 h-16" /> {/* Replace with the path to your logo */}
        <div className="flex items-center space-x-4">
          <div className="w-16 h-16 rounded-full bg-gradient-to-r from-black to-white"></div>
          <div className="text-black font-semibold text-xl">
            Presented by Nexrage Games
          </div>
        </div>
      </div>
    </div>
  );
};
