import React from "react";
import { Background } from "./Background/Background";
import { TitleText } from "./TitleText/TitleText";
import { Button } from "../../components/ui/common/Button";
import { Link } from "react-router-dom";
import planet from "../../assets/images/planet.png";


export const MainMenu = () => {
  return (
    <>
      <Background />
      <TitleText />
      {/* add image that is center of the screen and has a button on it */}
<div>
        <img src={planet} alt="capsule machine" className="w-[50%] h-[50%]"/>


      <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 ">
        <Link to="/capsule"> <Button text="START" /> </Link>
        <Link to="/gohome"> <Button text="GO HOME" /> </Link>
        <Link to="/options"> <Button text="OPTIONS"  /> </Link>
      </div>
      </div>
    </>
  );
};

export default MainMenu;
