import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

interface CarouselProps {
  imageList: string[];
}

export const Carousel: React.FC<CarouselProps> = ({imageList}) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 2000,
  };

  return (
    <div className='w-full h-full'>
      <Slider {...settings}>
        {imageList.map((imageUrl, index) => (
          <div key={index} className="h-[76vh] w-full flex items-center justify-center overflow-hidden">
            <img className="w-full h-full object-cover" src={imageUrl} alt={`carousel ${index + 1}`} />
          </div>
        ))}
      </Slider>
    </div>
  );
};
