import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../assets/images/capsule.jpg';

export const Intro = () => {
  const navigate = useNavigate();
  const [opacity, setOpacity] = useState(0);

  // Fade in effect
  useEffect(() => {
    const fadeIn = setTimeout(() => {
      setOpacity(1);
    }, 1000);

    return () => clearTimeout(fadeIn);
  }, []);

  // Redirect after 5 seconds
  useEffect(() => {
    const timeout = setTimeout(() => {
      setOpacity(0); // Fade out effect
      setTimeout(() => navigate('/main-menu'), 1000); // Redirect after fade out
    }, 5000);

    return () => clearTimeout(timeout);
  }, [navigate]);

  return (
    <div className="flex flex-col items-center justify-center bg-black transition-opacity duration-1000 ease-in-out" style={{ opacity }}>
      <p className="text-white text-xl md:text-3xl lg:text-5xl font-bold font-mario text-center mb-4">Presented By</p>
      <p className="text-white text-2xl md:text-4xl lg:text-6xl font-bold font-mario text-center mb-4">Nexrage Games</p>
      <img src={logo} alt="logo" className="w-8 h-8 animate-spin" />
    </div>
  );
};
