import React, { useEffect } from 'react';
import spinningCircle from '../assets/images/spinning-circle.svg';
import { useNavigate } from 'react-router-dom';

export const Intro = () => {
  const navigate = useNavigate();

  useEffect(() => {
    const timeout = setTimeout(() => {
      navigate('/'); // Redirect to the MainMenu after 5 seconds
    }, 5000);

    return () => clearTimeout(timeout);
  }, [navigate]);

  return (
    <div className="flex items-center justify-center bg-black h-screen">
      <div className="animate-spin">
        <img src={spinningCircle} alt="spinning circle" className="w-24 h-24" />
      </div>
      <p className="text-white text-xl ml-4">Presented by Nexrage Games</p>
    </div>
  );
};
