import React from "react";
import logo from '../assets/logo.svg'; // Assuming your logo is in the assets folder

interface IntroProps {
  onEnd: () => void;
}

export const Intro: React.FC<IntroProps> = ({ onEnd }) => {
  return (
    <div className="flex justify-center items-center h-screen bg-black">
      <img
        src={"https://placehold.co/400"}
        alt="logo"
        className="w-32 h-32 animate-spin-slow"
      />
      <button
        onClick={onEnd}
        className="absolute bottom-10 right-10 bg-blue-500 text-white p-2 rounded-md hover:bg-blue-700"
      >
        Skip Intro
      </button>
    </div>
  );
};
