import React from 'react';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";

interface CarouselProps {
  imageList: string[];
  text: string;
}

export const Carousel: React.FC<CarouselProps> = ({imageList, text}) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: false,
    autoplaySpeed: 2000,
  };

  return (
    <div className='w-full h-full flex p-8 shadow-lg p-4 rounded-3xl bg-[#FED7AA] m-8 border-2 border-orange-600'>
      <div className='w-1/2 h-full'>
        <Slider {...settings}>
          {imageList.map((imageUrl, index) => (
            <><div key={index} className="h-[80vh] w-full flex items-center justify-center overflow-hidden">
              <img className="w-full h-full object-cover" src={imageUrl} alt={`carousel ${index + 1}`} />
            </div><div className='w-1/2 h-full bg-gray-200 p-4'>
                <p className='text-gray-700'>{text}</p>
              </div></>
          ))}
        </Slider>
      </div>
    </div>
  );
};
